<?phpnamespace App\Http\Controllers;use Illuminate\Foundation\Bus\DispatchesJobs;use Illuminate\Routing\Controller as BaseController;use Illuminate\Foundation\Validation\ValidatesRequests;use Illuminate\Foundation\Auth\Access\AuthorizesRequests;use Illuminate\Support\Facades\App;use Illuminate\Support\Facades\Session;class Controller extends BaseController{    public function __construct()    {        if(Session::get('language')){            App::setLocale(Session::get('language'));        }    }    use AuthorizesRequests, DispatchesJobs, ValidatesRequests;}