<?phpnamespace App\Http\Controllers;use App\Category;use App\Faq;use App\Product;use Illuminate\Support\Facades\App;use Illuminate\Support\Facades\Session;use Illuminate\Http\Request;use Illuminate\Support\Facades\Auth;class HomeController extends Controller{    /**     * Create a new controller instance.     *     * @return void     */    /**     * Show the application dashboard.     *     * @return \Illuminate\Http\Response     */    public function index()    {        if(Session::get('language')){            App::setLocale(Session::get('language'));        }        $random_products[] = Product::inRandomOrder()->take(4)->get();        $products = Product::orderBy('created_at', 'desc')->take(3)->get();        return view('index', ['products'=>$products, 'random_products'=>$random_products]);    }    public function faq()    {        if(Session::get('language')){            App::setLocale(Session::get('language'));        }        $faq = Faq::all();        return view('faq', ['faq'=>$faq]);    }}