<?phpnamespace App;use Illuminate\Database\Eloquent\Model;class Product extends Model{    public function images(){        return $this->hasMany( Photo::class);    }    public function name()    {        if (config('app.locale') == 'en') {            return $this->brand;        }        if (config('app.locale') == 'ru') {            return $this->brand_ru;        }        if (config('app.locale') == 'fr') {            return $this->brand_fr;        }    }    public function desc()    {        if (config('app.locale') == 'en') {            return $this->description;        }        if (config('app.locale') == 'ru') {            return $this->description_ru;        }        if (config('app.locale') == 'fr') {            return $this->description_fr ;        }    }    protected $fillable = [        'category_id',        'photo_id',        'icon_id',        'brand',        'brand_ru',        'brand_fr',        'price',        'description',        'description_ru',        'description_fr',        'released_on'    ];}